/**
 * Contains fundamental classes and base classes that define commonly-used value and reference data types, events and event handlers, interfaces, attributes, and processing exceptions.
 * @see [source](https://docs.microsoft.com/dotnet/api/system)
 */
declare namespace System {
    // temporary
    type String = string;
    type Boolean = boolean;
    type Char = number;
    type Decimal = number;
    type Double = number;
    type Int32 = number;
    type Int64 = number;
    type Single = number;
    type UInt32 = number;
    type UInt64 = number;
}
